using PruebaPracticaGISSA.Shared;
using PruebaPracticaGISSA.Models;

namespace PruebaPracticaGISSA.Pages
{
    public partial class LoginPanel
    {
        private Login loginComponent;
        private bool messageShown = false;
        private string messageTitle = string.Empty;
        private string messageBody = string.Empty;
        private string messageImage = string.Empty;
        private MessageWindow.MessageType messageType;

        private void HandleLoginResult(bool result)
        {
            if (result)
            {
                showMessage("Login", "Usuario logueado correctamente.", "", MessageWindow.MessageType.Success);
                Navigation.NavigateTo("");
            }
            else
            {
                showMessage("Login", "Error al iniciar sesión, credenciales incorrectas.", "", MessageWindow.MessageType.Error);
            }
        }

        private void HandleForgotPassword()
        {
            showMessage("JA-JA", "", "data:image/jpeg;base64,", MessageWindow.MessageType.Warning);
        }

        private void HandleCreateAccount()
        {
            showMessage("Crear Usuario", "En construcción - Próximamente", "", MessageWindow.MessageType.Info);
        }

        private void showMessage(string title, string message, MessageWindow.MessageType type)
        {
            messageTitle = title;
            messageBody = message;
            messageType = type;
            messageShown = true;
        }

        private void showMessage(string title, string message, string imgUrl, MessageWindow.MessageType type)
        {
            messageTitle = title;
            messageBody = message;
            messageType = type;
            messageShown = true;
            messageImage = imgUrl;
        }

        private void hideMessage()
        {
            messageShown = false;
        }
    }
}